import React, { useState, useEffect } from "react";

const Cart = ({ cart: initialCartData, updateCartData }) => {
  const cartData = initialCartData || [];

  useEffect(() => {
    // Save the cart data to local storage whenever it changes
    localStorage.setItem("CartData", JSON.stringify(cartData));
  }, [cartData]);
  if (cart.length === 0) {
    return (
      <div>
        <div className="m-5 w-100 text-center fs-3">The Cart is Empty!</div>
      </div>
    );
  }
  let totalPrice = cart.reduce((total, cartItem) => total + cartItem.price, 0);
  const handleRemoveItem = (index) => {
    const updatedCartData = [...cart.slice(0, index), ...cart.slice(index + 1)];
    updateCartData(updatedCartData);
  };

  return (
    <div>
      <div className="container m-auto mt-5 table-responsive table-responsive-sm table-responsive-md">
        <table className="table table-hover">
          <thead className="text-success fs-4">
            <tr>
              <th scope="col">#</th>
              <th scope="col">Name</th>
              <th scope="col">Quantity</th>
              <th scope="col">Option</th>
              <th scope="col">Amount </th>
              <th scope="col"></th>
            </tr>
          </thead>
          <tbody>
            {cart.map((cartItem, index) => (
              <tr key={index}>
                <th scope="row">{index + 1}</th>
                <td style={{ color: "white" }}>{cartItem.foodItem.name}</td>
                <td style={{ color: "white" }}>{cartItem.qty}</td>
                <td style={{ color: "white" }}>{cartItem.size}</td>
                <td style={{ color: "white" }}>{cartItem.price}</td>
                <td style={{ color: "white" }}>
                  <button type="button" className="btn-p-0">
                    <img
                      src="trash.jpg"
                      alt="delete"
                      width="20px"
                      height="20px"
                      onClick={() => handleRemoveItem(index)}
                    />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div>
          <h1 className="fs-2">Total Price: {totalPrice}/-</h1>
        </div>
        <button className="btn bg-success mt-5">Check Out</button>
      </div>
    </div>
  );
};

export default Cart;
