import React from "react";
// import { useDispatchCart, useCart } from "../Component/ContextReducer";

const Cart = () => {
  const cartData = JSON.parse(localStorage.getItem("CartData"));
  const Data = JSON.parse(localStorage.getItem("Data"));
  console.log(Data);

  if (!cartData) {
    return (
      <div>
        <div className="m-5 w-100 text-center fs-3">The Cart is Empty!</div>
      </div>
    );
  }
  let totalPrice = cartData.reduce((total) => total + Data[2], 0);
  return (
    <div>
      <div className="container m-auto mt-5 table-responsive table-responsive-sm table-responsive-md">
        <table className="table table-hover">
          <thead className="text-success fs-4">
            <tr>
              <th scope="col">#</th>
              <th scope="col">Name</th>
              <th scope="col">Quantity</th>
              <th scope="col">Option</th>
              <th scope="col">Amount </th>
              <th scope="col"></th>
            </tr>
          </thead>
          <tbody>
            {cartData.map((food, index) => (
              <tr>
                <th scope="row">{index + 1}</th>
                <td style={{ color: "white" }}>{food.name}</td>
                <td style={{ color: "white" }}>{Data[0]}</td>
                <td style={{ color: "white" }}>{Data[1]}</td>
                <td style={{ color: "white" }}>{Data[2]}</td>
                <td style={{ color: "white" }}>
                  <button type="button" className="btn-p-0">
                    <img
                      src="trash.jpg"
                      alt="delete"
                      width="20px"
                      height="20px"
                      onClick={() => {
                        // dispatch({ type: "REMOVE", index: index });
                      }}
                    />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div>
          <h1 className="fs-2">Total Price: {totalPrice}/-</h1>
        </div>
        <button className="btn bg-success mt-5">Check Out</button>
      </div>
    </div>
  );
};

export default Cart;
