import React from "react";

const Cart = () => {
  const cartData = JSON.parse(localStorage.getItem("CartData"));

  if (!cartData) {
    return (
      <div>
        <div className="m-5 w-100 text-center fs-3">The Cart is Empty!</div>
      </div>
    );
  }

  let totalPrice = 0;

  return (
    <div>
      <div className="container m-auto mt-5 table-responsive table-responsive-sm table-responsive-md">
        <table className="table table-hover">
          {/* Table header here */}
          <tbody>
            {cartData.map((cartItem, index) => (
              <tr key={index}>
                <th scope="row">{index + 1}</th>
                <td style={{ color: "white" }}>{cartItem.name}</td>
                <td style={{ color: "white" }}>{cartItem.qty}</td>
                <td style={{ color: "white" }}>{cartItem.size}</td>
                <td style={{ color: "white" }}>
                  {cartItem.qty *
                    parseInt(cartItem.item.options[cartItem.size])}
                </td>
                <td style={{ color: "white" }}>
                  <button type="button" className="btn-p-0">
                    <img
                      src="trash.jpg"
                      alt="delete"
                      width="20px"
                      height="20px"
                      onClick={() => {
                        // Remove the item from cartData and update localStorage
                        const updatedCartData = cartData.filter(
                          (_, i) => i !== index
                        );
                        localStorage.setItem(
                          "CartData",
                          JSON.stringify(updatedCartData)
                        );
                      }}
                    />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div>
          <h1 className="fs-2">Total Price: {totalPrice}/-</h1>
        </div>
        <button className="btn bg-success mt-5">Check Out</button>
      </div>
    </div>
  );
};

export default Cart;
