import React from "react";
import Footer from "../Component/Footer";
import { useSelector } from "react-redux";

// Define a fallback image URL
const fallbackImageURL = "/path/to/fallback-image.jpg";

const MyOrder = () => {
  const cartData = useSelector((state) => state.cart.cartData);
  let totalPrice = cartData.reduce(
    (total, cartItem) => total + cartItem.price,
    0
  );

  return (
    <div>
      <div className="container mt-5">
        <h2 className="text-center mb-4">My Orders</h2>
        <div className="row">
          {cartData.length === 0 ? (
            <div className="col-md-12 text-center">
              <p>Your cart is empty.</p>
            </div>
          ) : (
            <div className="col-md-12">
              <div className="row">
                {cartData.map((item, index) => (
                  <div key={index} className="col-md-4 mb-4">
                    <div className="card">
                      {/* Use the fallbackImageURL as a default */}
                      <img
                        src={item.foodItem.img || fallbackImageURL}
                        className="card-img-top"
                        alt={item.foodItem.name}
                      />
                      <div className="card-body">
                        <h5 className="card-title">{item.foodItem.name}</h5>
                        <p className="card-text">
                          <strong>Quantity:</strong> {item.qty}
                          <br />
                          <strong>Size:</strong> {item.size}
                          <br />
                          <strong>Price:</strong> ${item.price.toFixed(2)}
                        </p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
              <div className="text-end mt-4">
                <h4>Total Price: ${totalPrice.toFixed(2)}</h4>
              </div>
            </div>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default MyOrder;
